const mongoose = require('mongoose')
const crypto = require('crypto')
const Schema = mongoose.Schema

const ApiSetSchema = new Schema({
    uid: { type: String },
    name: { type: String },
    domain: { type: String },
    description: { type: String },
    project_uid: { type: String },
    org_uid: { type: String },
    env_uid: { type: String },
    user_uid: { type: String },
    authorization: { type: Schema.Types.Mixed },
    apis: [{ type: mongoose.Schema.Types.ObjectId, ref: 'Api' }]

})

ApiSetSchema.pre('save', function (next) {
    var self = this
    var id = 'aps' + crypto.randomBytes(18).toString('hex')
    self.uid = id
    next()
})


mongoose.model('ApiSet', ApiSetSchema)
module.exports = ['ApiSet', ApiSetSchema]