const mongoose = require('mongoose');
Schema = mongoose.Schema;

const ConfigDataSchema = new Schema({
    uid: { type: String },
    name: { type: String },
    type: { type: String },
    user_uid: { type: String },
    flow_uid: { type: String },
    config_uid: { type: String },
    org_uid: { type: String },
    env_uid: { type: String },
    project_uid: { type: String },
    activity_id: { type: String },
    action: { type: String },
    version: { type: String },
    service: { type: String },
    auth: { type: String },
    connection: { type: String },
    param: { type: String },
    agent: { type: String },
    trigger: { type: String },
    url_executable_settings: { type: Schema.Types.Mixed }
}, { timestamps: { createdAt: 'created_at', updatedAt: 'updated_at' } })

mongoose.model('ConfigData', ConfigDataSchema)
module.exports = ['ConfigData', ConfigDataSchema]