const mongoose = require('mongoose');
Schema = mongoose.Schema;

const ConfigSchema = new Schema({
    uid: { type: String },
    type: { type: String },
    user_uid: { type: String },
    flow_uid: { type: String },
    org_uid: { type: String },
    env_uid: { type: String },
    project_uid: { type: String },
    activity_id: { type: String },
    action: { type: String },
    param: { type: Schema.Types.Mixed },
    version: { type: String },
    service: { type: String },
    trigger_uid: { type: String }
}, { timestamps: { createdAt: 'created_at', updatedAt: 'updated_at' } })

mongoose.model('ConfigSchema', ConfigSchema)
module.exports = ['ConfigSchema', ConfigSchema]