const mongoose = require('mongoose')
const Schema = mongoose.Schema

const OrganizationSchema = new Schema({
    uid: { type: String },
    type: { type: String },
    name: { type: String },
    avatar: { type: String, default: "/img/user-img2.svg" },
    default_role: { type: Schema.Types.ObjectId, ref: 'OrganizationRole' },
    sso_details: { type: Schema.Types.Mixed },
    enable_sso: { type: Boolean, default: false },
    user_uid: { type: String },
    owner_uid: { type: String },
    created_by: { type: String },
    updated_by: { type: String },
    deleted_at: { type: Date, default: null }
}, { timestamps: { createdAt: 'created_at', updatedAt: 'updated_at' } })

mongoose.model('Organization', OrganizationSchema)
module.exports = ['Organization', OrganizationSchema]