var mongoose = require('mongoose');
Schema = mongoose.Schema;

var OrganizationUserSchema = new Schema({
    uid: { type: String },
    org_uid: { type: String },
    user_uid: { type: String },
    email: { type: String },
    invitation: { type: Schema.Types.ObjectId, ref: 'OrganizationOwnerInvitation' },
    roles: [{ type: Schema.Types.ObjectId, ref: 'OrganizationRole' }],
    deleted_at: { type: Date, default: null }
}, { timestamps: { createdAt: 'created_at', updatedAt: 'updated_at' } })

mongoose.model('OrganizationUser', OrganizationUserSchema)
module.exports = ['OrganizationUser', OrganizationUserSchema]