const mongoose = require('mongoose');
const Schema = mongoose.Schema;

const SynergyAccountSchema = new Schema({
    uid                 : { type : String },
	name                : { type : String },
	used				: { type : Number, default: 0},
	allocated			: { type : Number},
	renew				: { type : Date},
    org                 : { type : Schema.Types.ObjectId, ref: 'Organization' },
    plan                : { type : Schema.Types.ObjectId, ref: 'ConsumerPlan' },
	user_count          : { type : Number, default: 0},
	users:[{ type: Schema.Types.ObjectId, ref: 'User' }],
    updated_at          : { type : Date },
    deleted_at          : { type : Date, default: null },
    created_at          : { type : Date }
});

mongoose.model('SynergyAccount', SynergyAccountSchema)
module.exports = ['SynergyAccount', SynergyAccountSchema]