
const mongoose = require('mongoose')
const Schema = mongoose.Schema

const TriggerSchema = new Schema({
  uid: { type: String },
  created_at: { type: Date },
  updated_at: { type: Date },
  name: { type: String },
  title: { type: String },
  deleted_at: { type: Date, default: null },
  version: { type: String },
  input: { type: String },
  icon: { type: String },
  output: { type: String },
  category: { type: [] },
  published: { type: Boolean, default: false },
  polling: { type: Boolean },
  auth_type: { type: String },
  mock_data: { type: String },
  development_phase: { type: String },
  express_enabled: { type: Boolean },
  advanced_enabled: { type: Boolean }
})

mongoose.model('Trigger', TriggerSchema)
module.exports = ['Trigger', TriggerSchema]